CREATE FUNCTION public.account_count() RETURNS trigger
    LANGUAGE plpgsql
    AS $$BEGIN
  IF TG_OP = 'INSERT' THEN
    UPDATE chain_info SET count = count + 1 WHERE name = 'accounts';
    RETURN NEW;
  ELSIF TG_OP = 'DELETE' THEN
    UPDATE chain_info SET count = count - 1 WHERE name = 'accounts';
    RETURN OLD;
  ELSE
    UPDATE chain_info SET count = 0 WHERE name = 'accounts';
    RETURN NULL;
  END IF;
END;$$;
